mapper.select:
select

from user_auth
where identity_type =#{identity_type} and identifier =#{identifier}

mapper.insert:
insert into user_auth (id, uid, identity_type, 
  identifier, credential, is_sync
  )
values (#{id,jdbcType=INTEGER}, #{uid,jdbcType=INTEGER}, #{identityType,jdbcType=TINYINT}, 
  #{identifier,jdbcType=VARCHAR}, #{credential,jdbcType=VARCHAR}, #{isSync,jdbcType=BIT}
  )
